INSERT INTO `product` VALUES ('1', 'Kem Lót SilkyGirl Instant Pore Fix Primer 01 Natural', '166000', '1', '1', null, null, '1', '11', 'images/uploads/products/nula_cosmetic_product_1636508382.jpg', '1', '1', '1', '<p><a href=\"https://cocolux.com/danh-muc/kem-lot-makeup-primer-i.9\"><strong>Kem L&oacute;t</strong></a>&nbsp;Che Lỗ Ch&acirc;n L&ocirc;ng SILKYGIRL Instant Pore Fix Primer M&agrave;u 01 Natural 15ml l&agrave; kem l&oacute;t đến từ thương hiệu mỹ phẩm&nbsp;<a href=\"https://cocolux.com/thuong-hieu/silkygirl-i.126\"><em><strong>SILKYGIRL</strong></em></a>&nbsp;từ Malaysia, chất kem mỏng nhẹ gi&uacute;p hỗ trợ l&agrave;m mờ v&agrave; thu nhỏ lỗ ch&acirc;n l&ocirc;ng hiệu quả, giữ lớp makeup l&acirc;u tr&ocirc;i cả ng&agrave;y d&agrave;i, đồng thời che khuyết điểm, kh&ocirc;ng g&acirc;y bết d&iacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/10/images/products/1633747061450-kem-lot-silkygirl-instant-pore-fix-primer-01-natural-2.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Loại da ph&ugrave; hợp:</strong></p>\r\n\r\n<ul>\r\n	<li>Sản phẩm ph&ugrave; hợp với mọi loại da.</li>\r\n</ul>\r\n\r\n<p><strong>Giải ph&aacute;p t&igrave;nh trạng da:</strong></p>\r\n\r\n<ul>\r\n	<li>Da c&oacute; nhiều khuyết điểm.</li>\r\n	<li>C&oacute; lỗ ch&acirc;n l&ocirc;ng to.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/10/images/products/1633747070012-kem-lot-silkygirl-instant-pore-fix-primer-01-natural-1.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>C&aacute;c ph&acirc;n tử nhỏ c&oacute; trong Primer gi&uacute;p tệp v&agrave;o da tạo hiệu ứng lỗ ch&acirc;n l&ocirc;ng thu nhỏ r&otilde; rệt, gi&uacute;p giữ lớp trang điểm l&acirc;u tr&ocirc;i.</li>\r\n	<li>Kết cấu gel mịn dễ t&aacute;n, thấm nhanh v&agrave;o da gi&uacute;p l&agrave;n mịn m&agrave;ng, che khuyết điểm, l&agrave;m mờ lỗ ch&acirc;n l&ocirc;ng.</li>\r\n	<li>C&ocirc;ng thức kh&ocirc;ng bết d&iacute;nh, l&ecirc;n da cảm gi&aacute;c nhẹ như kh&ocirc;ng.</li>\r\n	<li>Th&agrave;nh phần chứa Dimethicone gi&uacute;p l&agrave;m mềm da tự nhi&ecirc;n, kh&ocirc;ng g&acirc;y hại cho l&agrave;n da.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/10/images/products/1633747079021-kem-lot-silkygirl-instant-pore-fix-primer-01-natural-3.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>15ml</p>\r\n\r\n<p><strong>Xuất xứ:&nbsp;</strong>Malaysia</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>SILKYGIRL</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong></p>\r\n\r\n<p>Lấy lượng vừa đủ sản phẩm khoảng 1-2 hạt đậu.</p>\r\n\r\n<p>Nhẹ nh&agrave;ng apply l&ecirc;n c&aacute;c khu vực c&oacute; nhiều khuyết điểm.</p>\r\n\r\n<p>D&ugrave;ng tay sạch nhẹ nh&agrave;ng t&aacute;n đều sản phẩm.</p>\r\n\r\n<p>Tiếp tục bước trang điểm nền hằng ng&agrave;y.</p>', '9993');
INSERT INTO `product` VALUES ('2', 'Phấn Phủ Bột Kiềm Dầu Innisfree No Sebum Mineral Powder 5g', '145000', '1', '1', null, null, '1', '0', 'images/uploads/products/nula_cosmetic_product_1636508523.jpg', '1', '1', '1', '<p><em><strong>Phấn Phủ&nbsp;Innisfree Dạng Bột No Sebum Mineral Powder</strong></em>&nbsp;l&agrave; phấn kho&aacute;ng dạng bột. Gần như kh&ocirc;ng l&ecirc;n t&ocirc;ng tr&ecirc;n da. N&ecirc;n d&ugrave;ng để dặm v&ugrave;ng chữ T, h&uacute;t dầu thừa tr&ecirc;n da trong ng&agrave;y. Vừa kh&ocirc;ng bị d&agrave;y phấn, b&iacute; da, lại vừa nhẹ mặt, h&uacute;t dầu cực &ecirc;m &aacute;i. Phấn c&oacute; thể phủ l&ecirc;n bầu mắt thay thế cho Kem L&oacute;t Mắt, Dặm phấn v&agrave;o ch&acirc;n t&oacute;c để h&uacute;t bớt dầu thừa, c&oacute; t&aacute;c dụng như dầu gội kh&ocirc; lu&ocirc;n nữa!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1632120086057-phan-phu-bot-kiem-dau-innisfree-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://cocolux.com/thuong-hieu/innisfree-i.255\"><strong>Innisfree</strong></a>&nbsp;l&agrave; một thương hiệu mỹ phẩm chiết xuất từ thi&ecirc;n nhi&ecirc;n thuộc thuộc Tập đo&agrave;n mỹ phẩm AmorePacific của H&agrave;n Quốc. Được th&agrave;nh lập v&agrave;o năm 2000. Sản phẩm của h&atilde;ng đa dạng từ đồ skincare đến make up, ph&ugrave; hợp với mọi loại da kể cả da nhạy cảm v&igrave; được chiết xuất từ những nguy&ecirc;n liệu tự nhi&ecirc;n</p>\r\n\r\n<p><em><strong>Loại da ph&ugrave; hợp:</strong></em>&nbsp;Da dầu</p>\r\n\r\n<p><em><strong>Finish:</strong></em>&nbsp;hầu như kh&ocirc;ng l&ecirc;n t&ocirc;ng, h&uacute;t dầu thừa</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1632120093952-phan-phu-bot-kiem-dau-innisfree-2.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Ki&ecirc;̉m soát d&acirc;̀u nhờn vượt tr&ocirc;̣i, giữ được lớp makeup l&acirc;u tr&ocirc;i đ&ecirc;́n 24 giờ đ&ocirc;̀ng h&ocirc;̀</li>\r\n	<li>Độ che phủ hiệu quả, những hạt phấn nhỏ t&aacute;c dụng h&uacute;t nhiều dầu, mồ h&ocirc;i; c&oacute; khả năng điều chỉnh độ ẩm tr&ecirc;n da gi&uacute;p da mềm mại, th&ocirc;ng tho&aacute;ng kh&ocirc;ng bị b&iacute; dầu</li>\r\n	<li>Tone m&agrave;u trắng trong su&ocirc;́t, l&ecirc;n da kh&ocirc;ng có màu, giúp da tự nhi&ecirc;n đầy sức sống</li>\r\n	<li>Ngo&agrave;i ra em &yacute; c&ograve;n c&oacute; nhiều c&ocirc;ng dụng hơn nữa: c&oacute; thể ngăn mồ h&ocirc;i v&ugrave;ng da dưới c&aacute;nh tay, chữa ch&aacute;y cho m&aacute;i t&oacute;c của bạn nếu bạn kh&ocirc;ng kịp gội đầu, mix với nhũ l&agrave;m bắt s&aacute;ng cho mặt hoặc body,...</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1632120113578-phan-phu-bot-kiem-dau-innisfree-3.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Trọng lượng:&nbsp;</strong>5g</p>\r\n\r\n<p><strong>Xuất xứ:</strong>&nbsp;H&agrave;n Quốc</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Innisfree</p>', '9999');
INSERT INTO `product` VALUES ('3', 'Son lì Maybelline Creamy Mattes MNU 09 Chili Nude', '178000', '1', '2', null, null, '1', '10', 'images/uploads/products/nula_cosmetic_product_1636513435.jpg', '1', '1', '1', '<p><strong>TH&Ocirc;NG TIN SẢN PHẨM</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Son l&igrave; Maybelline Creamy Mattes MNU 09 Chili Nude</strong></p>\r\n\r\n<p><strong>Maybelline Color Sensational Inti-Matte Nudes Lipstick</strong>&nbsp;l&agrave; bộ sưu tập son Nude mới nhất c&ugrave;ng 10 m&agrave;u son nude thời thượng, ph&ugrave; hợp với mọi t&ocirc;ng da của c&aacute;c c&ocirc; n&agrave;ng Ch&acirc;u &Aacute;. Son l&igrave; Maybelline xuất hiện đ&igrave;nh đ&aacute;m l&agrave;m khuấy đảo cộng đồng mạng, c&ograve;n được v&iacute; như Nars, hay MAC thần th&aacute;nh m&agrave; gi&aacute; cả lại v&ocirc; c&ugrave;ng b&igrave;nh d&acirc;n. C&ograve;n chần chờ g&igrave; nữa! Nhanh tay tậu cho m&igrave;nh một em về nh&agrave; ngay nh&eacute;!<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1616729320994-son-li-maybelline-creamy-mattes-chilli-nude-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<strong>Maybelline New York</strong>&nbsp;l&agrave; c&ocirc;ng ty mỹ phẩm h&agrave;ng đầu tại Mỹ. Khởi nguồn từ việc b&aacute;n mascara qua thư điện tử, mascara&nbsp;<strong>Maybelline</strong>&nbsp;nhanh ch&oacute;ng trở th&agrave;nh m&oacute;n đồ kh&ocirc;ng thể thiếu được của c&aacute;c c&ocirc; g&aacute;i. Ng&agrave;y nay, mascara Maybelline l&agrave; mascara được y&ecirc;u th&iacute;ch của nước Mỹ v&agrave; được sự tin tưởng của c&aacute;c chuy&ecirc;n gia trang điểm h&agrave;ng đầu thế giới. Kh&ocirc;ng chỉ dừng lại ở đấy,&nbsp;<strong>Maybelline</strong>&nbsp;c&ograve;n mở rộng cung cấp bộ sưu tập trang điểm về to&agrave;n phương diện. Bạn sẽ lu&ocirc;n t&igrave;m thấy mọi thứ mới mẻ v&agrave; hữu &iacute;ch từ&nbsp;<strong>Maybelline</strong>.<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1616729284851-son-li-maybelline-creamy-mattes-chilli-nude.jpeg\" /></p>\r\n\r\n<p><br />\r\n<strong>Maybelline Color Sensational Inti-Matte Nudes Lipstick</strong>&nbsp;với chất son l&igrave; matte nhưng lại creamy, khiến l&ecirc;n m&ocirc;i vẫn mềm mịn m&agrave; kh&ocirc;ng hề g&acirc;y cảm gi&aacute;c kh&ocirc; r&aacute;p hay để lại c&aacute;c r&atilde;nh m&ocirc;i kh&oacute; chịu.&nbsp;Cho d&ugrave; l&agrave; m&ocirc;i kh&ocirc; tới đ&acirc;u đi chăng nữa, cũng sẽ h&agrave;i l&ograve;ng khi kho&aacute;c tr&ecirc;n m&igrave;nh những m&agrave;u son y&ecirc;u kiều, l&acirc;u tr&ocirc;i, l&acirc;u phai cả ng&agrave;y d&agrave;i của&nbsp;<strong>Maybelline</strong>. Hương thơm ngọt lịm v&agrave; khả năng b&aacute;m m&agrave;u l&ecirc;n đến 24h đồng hồ.</p>\r\n\r\n<p><br />\r\n<strong>Maybelline Color Sensational Matte Lipstick</strong>&nbsp;l&agrave; d&ograve;ng son l&igrave;, l&ecirc;n m&agrave;u cực chuẩn, giữ m&agrave;u hơn 5 tiếng. Với ch&acirc;́t son m&ecirc;̀m, mượt, mịn. Son lướt tr&ecirc;n m&ocirc;i m&ecirc;̀m, cảm nh&acirc;̣n đ&ocirc;̣ mịn màng &acirc;̉m &acirc;̉m như bơ, cực th&iacute;ch nh&eacute;. Son c&oacute; độ dưỡng nhẹ, kh&ocirc;ng l&agrave;m kh&ocirc; m&ocirc;i, c&oacute; m&ugrave;i hương rất ngọt ng&agrave;o, dịu nhẹ.<br />\r\n&nbsp;</p>\r\n\r\n<p><br />\r\n<strong>Son l&igrave; Maybelline Creamy Mattes MNU 09 Chili Nude</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1616729265152-son-li-maybelline-creamy-mattes-chilli-nude.jpeg\" /></p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1616729272332-son-li-maybelline-creamy-mattes-nude-9.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<strong>Xuất xứ :</strong>&nbsp;Mỹ</p>\r\n\r\n<p><strong>Thương hiệu</strong>&nbsp;: Maybeline</p>\r\n\r\n<p><strong>Mỹ phẩm Nula Cosmetic</strong></p>', '9999');
INSERT INTO `product` VALUES ('4', 'Phấn nước Lime V Collagen Ample SPF 50+ - 10', '295000', '1', '1', null, null, '1', '0', 'images/uploads/products/nula_cosmetic_product_1636508729.jpg', '1', '1', '1', '<p>Phấn nước Lime Collagen Ample&nbsp;<em><strong>Cushion</strong></em>&nbsp;c&oacute; xuất xứ từ H&agrave;n Quốc,trọng lượng 20g gi&uacute;p với th&agrave;nh phần c&oacute; chứa collagen gi&uacute;p cho da đ&agrave;n hồi, tr&aacute;nh được việc tạo nếp nhăn m&agrave; lại giữ cho da c&oacute; được lớp nền căng mịn suốt cả ng&agrave;y, &nbsp;Lime&nbsp;V Collagen ch&iacute;nh l&agrave; sự lựa chọn ho&agrave;n hảo cho c&aacute;c n&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2020/12/products/1609311981906-phan-nuoc-lime-xanh-20-(hop)-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sản phẩm hội tụ chức năng của 6-in-1 vừa l&agrave; kem chống nắng, kem nền, lớp l&oacute;t, highlight, che khuyết điểm, cấp ẩm, c&oacute; thể thay thế cho những bước trang điểm cầu k&igrave; phức tạp m&agrave; vẫn đảm bảo cho chị em lớp nền mịn đẹp cực th&iacute;ch, lại c&ograve;n tiết kiệm được rất nhiều thời gian v&agrave; tiền bạc. Lớp kem tạo cảm gi&aacute;c m&aacute;t lạnh ngay từ lần đầu apply l&ecirc;n da, kết hợp với b&ocirc;ng m&uacute;t cực mềm gi&uacute;p kem thẩm thấu nhanh, kh&ocirc;ng hề g&acirc;y nặng mặt, kh&ocirc;ng cakey m&agrave; rất tự nhi&ecirc;n v&agrave; tươi s&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;Được chiết xuất từ hoa sen trắng gi&uacute;p tăng độ đ&agrave;n hồi cho da, dưỡng da trắng s&aacute;ng hơn.</p>\r\n\r\n<p>-&nbsp;Phức hợp xanh bao gồm c&oacute; Hương phong thảo- Bergamot, Lan Nam Phi- Freesia, hoa C&uacute;c &ndash; Chamomile, bạc h&agrave;, Hương thảo gi&uacute;p da trong suố, tinh khiết, l&agrave;m mềm da v&agrave; tăng khả năng phục hồi sức đề kh&aacute;ng cho da được khỏe mạnh.</p>\r\n\r\n<p>- Tinh chất dưỡng cao cấp Bifida fermented solution gi&uacute;p nu&ocirc;i dưỡng da từ s&acirc;u b&ecirc;n trong, hỗ trợ l&agrave;m mờ c&aacute;c vết th&acirc;m do mụn v&agrave; l&agrave;m mụn mau lặn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1617942714579-phan-nuoc-lime-v-collagen.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;Với th&agrave;nh phần chứa collagen l&agrave; chất gi&agrave;u protein c&oacute; khả năng hấp thụ độ ẩm từ c&aacute;c tế b&agrave;o của da v&agrave; kết nối ch&uacute;ng với nhau, tạo ra sự li&ecirc;n kết chặt chẽ giữa những tế b&agrave;o, nhờ đ&oacute; tăng cường độ ẩm, độ đ&agrave;n hồi c&acirc;n bằng cho da.</p>\r\n\r\n<p>-&nbsp;Niacinamide l&agrave; chất dưỡng trắng da an to&agrave;n, hiệu quả, gi&uacute;p da trắng s&aacute;ng, đều m&agrave;u hơn.</p>\r\n\r\n<p>Team Nula Cosmetic gửi bạn tham khảo 2 d&ograve;ng</p>\r\n\r\n<p><strong>V Collagen Ample (m&agrave;u xanh):</strong>&nbsp;d&agrave;nh cho da thường, da kh&ocirc;. Lớp Finish căng b&oacute;ng tạo cảm gi&aacute;c da khỏe mạnh, glow chứ kh&ocirc;ng b&oacute;ng nhẫy.</p>\r\n\r\n<p><strong>Real Cover Pink (m&agrave;u trắng):&nbsp;</strong>d&agrave;nh cho da hỗn hợp, da dầu. Lớp Finish l&igrave; hơn so với d&ograve;ng Ample.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện Nula Cosmetic&nbsp;c&oacute; 2 tone:</p>\r\n\r\n<p>- Tone 10: Da s&aacute;ng</p>\r\n\r\n<p>- Tone 20: Da thường</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2020/12/products/1609312114250-phan-nuoc-lime-xanh-20-(hop)-3.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;-&nbsp;<em><strong>LIME</strong></em>&nbsp;V Collagen Ample&nbsp;Cushion&nbsp;với chức năng chống l&atilde;o ho&aacute; da hiệu quả, đ&atilde; đẩy sản phẩm l&ecirc;n một tầm mới m&agrave; ch&uacute;ng ta c&oacute; thể n&oacute;i đ&acirc;y ch&iacute;nh l&agrave; cushion hiệu quả gấp 3 lần.</p>\r\n\r\n<p>&nbsp;- Dưỡng ẩm cho da, l&agrave;m trắng da v&agrave; xo&aacute; mờ c&aacute;c l&agrave;n da ko đều m&agrave;u, Collagen gi&uacute;p da bạn đ&agrave;n hồi v&agrave; chống t&igrave;nh trạng da nhăn nheo.</p>\r\n\r\n<p>&nbsp;- Cushion Lime collagen c&ograve;n kh&aacute;c phục được c&aacute;c khuyết điểm của c&aacute;c loại cushion kh&aacute;c như độ che phủ ho&agrave;n hảo c&aacute;c khuyết điểm một c&aacute;ch vượt trội&nbsp;</p>\r\n\r\n<p>- Collagen l&agrave; một th&agrave;nh phần gi&agrave;u protein c&oacute; khả năng hấp thụ chất ẩm từ những tế b&agrave;o của da v&agrave; kết nối ch&uacute;ng với nhau, tạo sự li&ecirc;n kết chặt chẽ giữa c&aacute;c tế b&agrave;o. Từ đ&oacute; tăng cường lớp m&agrave;ng ẩm v&agrave; độ đ&agrave;n hồi c&acirc;n bằng của da.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1615973602415-phan-nuoc-lime-2.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Loại da ph&ugrave; hợp: Da thường, da kh&ocirc;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dung lượng:</strong>&nbsp;20g</p>\r\n\r\n<p><strong>Xuất xứ:</strong>&nbsp;H&agrave;n Quốc</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;LIME</p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong></p>\r\n\r\n<p>Dùng b&ocirc;ng ph&acirc;́n Lime V Collagen Ample&nbsp;Cushion&nbsp;nhẹ mi&ecirc;́ng mút b&ecirc;n trong h&ocirc;̣p ph&acirc;́n, sau đó thoa nhẹ nhàng khắp vùng mặt và c&ocirc;̉. Chỉ mất 5 ph&uacute;t là bạn đ&atilde; c&oacute; khu&ocirc;n mặt thật rạng rỡ mỗi ng&agrave;y r&ocirc;̀i nhé!</p>', '100');
INSERT INTO `product` VALUES ('5', 'Son Dưỡng Dior Full size 020', '550000', '1', '2', null, null, '1', '19', 'images/uploads/products/nula_cosmetic_product_1636513522.png', '1', '2', '1', '<p><strong>TH&Ocirc;NG TIN SẢN PHẨM</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Son Dưỡng M&ocirc;i Dior Collagen Addict Lip Maximizer 020 Pink d&ograve;ng son chuy&ecirc;n dưỡng mới nhất của Dior vừa cho ra mắt. So với d&ograve;ng của th&igrave; Dior Addict Lip maximize c&ograve;n được bổ sung them collagen gi&uacute;p t&aacute;i cấu tr&uacute;c lại m&agrave;u m&ocirc;i nhờ v&agrave;o hyaluronic acid với chất l&agrave;m đầy water-based l&agrave;m mướt, ẩm v&agrave; căng m&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/10/images/products/1634712910486-son-duong-dior-full-size-020-3.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&uacute;p mềm mịn tươi tắn cho đ&ocirc;i m&ocirc;i, dưỡng ẩm m&ocirc;i, trị bong tr&oacute;c v&agrave; kh&ocirc; m&ocirc;i</p>\r\n\r\n<p>Một lớp son b&oacute;ng đặc trưng kết hợp với m&agrave;u n&acirc;u sang trọng mang đậm chất ri&ecirc;ng của nh&agrave; Dior. Thỏi son đem đến sự thoải m&aacute;i khi đ&aacute;nh nhưng ko k&eacute;m phần tỏa s&aacute;ng với hiệu ứng l&agrave;m căng tức th&igrave; v&agrave; l&acirc;u d&agrave;i.&nbsp;</p>\r\n\r\n<p>Tạo cho đ&ocirc;i m&ocirc;i căng mọng đầy đặn&nbsp;</p>\r\n\r\n<p>Sắc n&acirc;u khiến đ&ocirc;i m&ocirc;i trở n&ecirc;n quyến rũ hơn</p>\r\n\r\n<p>M&ugrave;i hương bạc h&agrave; mang lại cho đ&ocirc;i m&ocirc;i cảm gi&aacute;c sảng kho&aacute;i, m&aacute;t lạnh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/10/images/products/1634712938425-son-duong-dior-full-size-020-2.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave;ng Dior Lip Maximizer trong 15 ng&agrave;y liền th&igrave; m&ocirc;i sẽ mềm mại v&agrave; bớt th&acirc;m.</p>\r\n\r\n<p>Đồng thời những vết r&atilde;nh m&ocirc;i cũng được l&agrave;m đầy hơn.</p>\r\n\r\n<p>Texture dạng lỏng, dễ d&agrave;ng apply. Cọ lấy son thu&ocirc;n d&agrave;i dễ lấy son</p>\r\n\r\n<p>Thiết kế đơn giản, thanh lịch, mang thương hiệu ri&ecirc;ng, nh&igrave;n được m&agrave;u son b&ecirc;n trong</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/10/images/products/1634712952856-son-duong-dior-full-size-020-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Trọng lượng:</strong>&nbsp;6ml</p>\r\n\r\n<p><strong>Xuất xứ:</strong>&nbsp;Ph&aacute;p</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Dior</p>\r\n\r\n<p><strong>Cửa h&agrave;ng mỹ phẩm</strong></p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong></p>\r\n\r\n<p>Một lớp son b&oacute;ng đặc trưng kết hợp với m&agrave;u n&acirc;u sang trọng mang đậm chất ri&ecirc;ng của nh&agrave; Dior. Thỏi son đem đến sự thoải m&aacute;i khi đ&aacute;nh nhưng ko k&eacute;m phần tỏa s&aacute;ng với hiệu ứng l&agrave;m căng tức th&igrave; v&agrave; l&acirc;u d&agrave;i.&nbsp;</p>', '999');
INSERT INTO `product` VALUES ('16', 'Set Phấn Bắt Sáng Và Son Bóng Fenty Beauty', '440000', null, '1', null, null, null, '0', 'images/uploads/products/nula_cosmetic_product_1636509159.jpg', null, '1', '1', '<p>Fenty Beauty Là m&ocirc;̣t thương hi&ecirc;̣u mĩ ph&acirc;̉m đ&ecirc;́n từ giọng ca đ&acirc;̀y cá tín &ndash; ca sĩ Rihanna. Fenty Beauty được c&ocirc; ca sĩ tài năng nghi&ecirc;n cứu su&ocirc;́t 2 năm. V&agrave; sau nhi&ecirc;̀u l&acirc;̀n tung ảnh b&acirc;̣t mí thì đ&ecirc;́n đ&acirc;̀u tháng 9 năm 2017, dòng&nbsp;<a href=\"https://cocolux.com/\"><em><strong>mĩ ph&acirc;̉m</strong></em></a>&nbsp;đ&ocirc;̣c đáo này đã chính thức trình làng. Trong khoảng thời gian ra mắt đ&ecirc;́n khi người h&acirc;m m&ocirc;̣ được trực ti&ecirc;́p sử dụng sản ph&acirc;̉m, Rihanna chưa từng khi&ecirc;́n c&ocirc;ng chúng phải bu&ocirc;ng lời th&acirc;́t vọng. Ch&iacute;nh vì ch&acirc;́t lượng sản ph&acirc;̉m t&ocirc;́t cũng như đi&ecirc;̉m đ&ocirc;̣t phá ri&ecirc;ng bi&ecirc;̣t. V&agrave; gi&ocirc;́ng như ánh sáng từ giọng ca &ldquo;Diamond&rdquo; m&agrave; mọi người y&ecirc;u mến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/08/images/products/1629424799470-set-phan-bat-sang-va-son-bong-fenty-beauty.jpeg\" /></p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1615532218551-set-phan-bat-sang-va-son-bong-fenty-beauty-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Diamond Bomb Baby Mini Lip Gloss and Highlighter Set by Rihanna về h&agrave;ng đẹp thi&ecirc;u rụi con tym</p>\r\n\r\n<p>Siêu phẩm highlight đình đám Fenty Beauty Diamond Bomb All-Over Diamond Veil với chất phấn nhũ cực xịn và thiết kế sang chảnh được các tín đồ làm đẹp review là &ldquo;không còn gì để chê&rdquo;. Thiết kế lục gi&aacute;c quen thuộc nay được thu nhỏ hơn nhưng vẫn giữ được chất &quot;con nh&agrave; n&ograve;i&quot; kh&ocirc;ng đụng h&agrave;ng bất cứ ai</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/03/products/1615532236541-set-phan-bat-sang-va-son-bong-fenty-beauty-2.jpeg\" /></p>\r\n\r\n<p><br />\r\n<strong>Xuất xứ:&nbsp;</strong>Mỹ<br />\r\n<strong>Thương hiệu:</strong>&nbsp;Fenty Beauty</p>', null);
INSERT INTO `product` VALUES ('17', 'Phấn mắt Clio Pro Eye Palette 07 Peach Groove', '275000', null, '1', null, null, null, '0', 'images/uploads/products/nula_cosmetic_product_1636509447.jpg', null, '1', '1', '<p><strong>TH&Ocirc;NG TIN SẢN PHẨM</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thuộc bộ sưu tập c&aacute;c&nbsp;<strong>bảng phấn mắt</strong>&nbsp;HOTTEST v&agrave; BEST SELLER nhất của CLIO &ndash; CORAL TALK mang phong c&aacute;ch tự nhi&ecirc;n với tone hồng &ndash; cam đ&agrave;o , ph&ugrave; hợp cho những makeup look sử dụng cho mọi hoạt động như đi l&agrave;m, đi chơi.</p>\r\n\r\n<p>Bảng Phấn Mắt&nbsp;<strong>Clio</strong>&nbsp;Pro Eye Palette : Gồm nhiều t&ocirc;ng m&agrave;u nổi bật, mang lại phong c&aacute;ch năng động, c&aacute; t&iacute;nh. Sử dụng m&agrave;u mắt ở bảng m&agrave;u n&agrave;y gi&uacute;p bạn g&acirc;y ch&uacute; &yacute; được nhiều hơn bởi những t&ocirc;ng m&agrave;u ấn tượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631606307108-phan-mat-clio-pro-eye-palette-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bảng phấn mắt Clio Pro Eye Palette được chiết xuất từ c&aacute;c th&agrave;nh phần tự nhi&ecirc;n tăng cường chất kho&aacute;ng, th&ecirc;m v&agrave;o đ&oacute; cấu tr&uacute;c hạt phấn si&ecirc;u nhỏ c&ograve;n tăng cường khả năng b&aacute;m d&iacute;nh, mang lại m&agrave;u mắt tự nhi&ecirc;n.</p>\r\n\r\n<p>Đặc biệt, phấn c&ograve;n c&oacute; khả năng thấm h&uacute;t b&atilde; nhờn, do đ&oacute; bạn kh&ocirc;ng phải lo lắng t&igrave;nh trạng phấn bị tr&ocirc;i, bong tr&oacute;c do hoạt động qu&aacute; nhiều g&acirc;y đổ mồ h&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631783711465-phan-mat-clio-2.jpeg\" /></p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631783710805-phan-mat-clio-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c t&ocirc;ng m&agrave;u đa dạng từ m&agrave;u s&aacute;ng,