INSERT INTO `product` VALUES ('27', 'Mascara 2 Đầu Silkygirl Double Take Waterproof Duo', '248000', null, '1', null, null, null, '11', 'images/uploads/products/nula_cosmetic_product_1636510505.jpg', null, '1', '1', '<p><strong>TH&Ocirc;NG TIN SẢN PHẨM</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mascara Silkygirl 2 Đầu</strong>&nbsp;L&agrave;m Cong, D&agrave;y &amp; Định H&igrave;nh Mi Double Take Waterproof Duo Mascara l&agrave; d&ograve;ng mascara đến từ thương hiệu&nbsp;<em><strong>mỹ phẩm Silkygirl</strong></em>&nbsp;của Malaysia, với thiết kế đầu chuốt 2 đầu mang lại h&agrave;ng mi tr&ecirc;n v&agrave; dưới cong quyến rũ, gi&uacute;p đ&ocirc;i mắt to tr&ograve;n thu h&uacute;t mọi &aacute;nh nh&igrave;n c&ugrave;ng với khả năng chống lem, chống tr&ocirc;i gi&uacute;p bạn tự tin suốt cả ng&agrave;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/01/products/1611203095021-mascara-2-dau-silkygirl-double-take-waterproof-duo-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Loại da ph&ugrave; hợp:&nbsp;</strong>Sản phẩm ph&ugrave; hợp với mọi loại da.</p>\r\n\r\n<p><strong>Giải ph&aacute;p t&igrave;nh trạng:</strong></p>\r\n\r\n<p>- L&ocirc;ng mi mỏng, ngắn, thưa thớt.</p>\r\n\r\n<p>- Thường xuy&ecirc;n trang điểm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/01/products/1611203104128-mascara-2-dau-silkygirl-double-take-waterproof-duo-2.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Chứa c&aacute;c th&agrave;nh phần gi&agrave;u dưỡng chất c&oacute; thể dễ d&agrave;ng h&ograve;a quyện v&agrave;o l&ocirc;ng mi, l&agrave;m đẹp v&agrave; an to&agrave;n cho đ&ocirc;i mắt.</p>\r\n\r\n<p>-&nbsp;<a href=\"https://cocolux.com/danh-muc/mascara-i.17\"><em><strong>M</strong></em></a><em><strong>ascara</strong></em>&nbsp;l&agrave;m d&agrave;i v&agrave; cong mi ngay lập tức, hiệu quả như &yacute;.</p>\r\n\r\n<p>- L&agrave;m d&agrave;y, d&agrave;