INSERT INTO `product` VALUES ('78', 'Tinh Chất Trị Mụn Trứng Cá Mờ Sẹo Eucerin ProAcne 30ml', '590000', null, '3', null, null, null, '10', 'images/uploads/products/nula_cosmetic_product_1636517884.png', null, '1', '1', '<p><strong>TH&Ocirc;NG TIN SẢN PHẨM</strong></p>\r\n\r\n<p><br />\r\n<br />\r\nTinh Chất Kem&nbsp;Trị Mụn Trứng C&aacute;, Mờ Sẹo Eucerin Pro Acne Solution gi&uacute;p cho da hấp thụ c&aacute;c hoạt chất tốt một c&aacute;ch nhanh ch&oacute;ng, cải thiện da v&agrave; l&agrave;m thu nhỏ lỗ ch&acirc;n l&ocirc;ng một c&aacute;ch hiệu quả do c&oacute; chứa Lactic Acid. Ngo&agrave;i Lactic Acid c&oacute; trong tinh chất trị mụn Eucerin c&oacute; chứa một th&agrave;nh phần đặc biệt đ&oacute; l&agrave; Decandiol l&agrave; chất c&oacute; t&aacute;c dụng diệt khuẩn v&agrave; ngăn ngừa da bị mụn trở lại. Sử dụng sảnphẩm một c&aacute;ch đều đặn v&agrave; thường xuy&ecirc;n sẽ gi&uacute;p cho l&agrave;n da của bạn trở n&ecirc;n khỏe khoắn, mịn m&agrave;ng v&agrave; tươi s&aacute;ng hơn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1632970379198-tinh-chat-tri-mun-trung-ca-mo-seo-eucerin-proacne-30ml.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Aqua, Cyclomethicone, Glycerin, Tapioca Starch, Methylpropanediol, Lactic Acid, Dimethiconol, Xanthan Gum, Decylene Glycol, Parfum, Sodium Stearoyl Glutamate, Sodium Chloride</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1632970396434-tinh-chat-tri-mun-trung-ca-mo-seo-eucerin-proacne-30ml-1.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Lactic Acid: gi&uacute;p kh&aacute;ng khuẩn, giảm mụn trứng c&aacute;, mụn mủ, t&aacute;i tạo lại bề mặt da một c&aacute;ch hiệu quả. Ngo&agrave;i ra c&ograve;n gi&uacute;p th&ocirc;ng tắc lỗ ch&acirc;n l&ocirc;ng , giảm vi khuẩn, loại bỏ tẩy da chết v&agrave; ngăn mụn