INSERT INTO `product` VALUES ('96', 'Sữa Tắm Senka Perfect Bubble for Body Floral+ 500ml', '149000', null, '4', null, null, null, '10', 'images/uploads/products/nula_cosmetic_product_1636535391.png', null, '2', '1', '<p><strong>TH&Ocirc;NG TIN SẢN PHẨM</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&atilde;ng mỹ phẩm Senka Perfect Whip n&ocirc;̉i ti&ecirc;́ng trong 1 v&agrave;i năm trở lại đ&acirc;y. Hơn th&ecirc;́ nữa, so với nhi&ecirc;̀u dòng sản ph&acirc;̉m cùng c&ocirc;ng dùng đ&oacute;n nhận được rất nhiều sự y&ecirc;u th&iacute;ch của người ti&ecirc;u dùng tại Việt Nam. Phải chẳng bởi sản phẩm của Senka được chứng nh&acirc;̣n bởi các chuy&ecirc;n gia hàng đ&acirc;̀u Nh&acirc;̣t Bản với đặc trưng v&ocirc; c&ugrave;ng l&agrave;nh t&iacute;nh, thiết kế sản phẩm th&ocirc;ng minh dễ sử dụng, mẫu m&atilde; sản ph&acirc;̉m đa dạng,&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1630918241250--sua-tam-senka-perfect.png\" /></p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p>Nay h&atilde;ng lại giới thiệu đ&ecirc;́n quý khách hàng tại Nh&acirc;t Bản th&ecirc;m 1 sản phẩm v&ocirc; c&ugrave;ng th&ocirc;ng dụng v&agrave; quả th&acirc;̣t si&ecirc;u ph&acirc;̉m này hứa hẹn sẽ rất được ưa th&iacute;ch tại thị trường Việt Nam trong thời gian tới. Đ&oacute; ch&iacute;nh l&agrave; si&ecirc;u ph&acirc;̉m sữa tắm hàng đ&acirc;̀u Nh&acirc;̣t Bản có dung tích l&ecirc;n tới 500ml đã chính thức ra mắt với quý chị em trong thời gian vừa qua.</p>\r\n\r\n<p>Sữa tắm hãng Senka còn được nh&acirc;̣n định l&agrave; d&ograve;ng sữa tắm nổi tiếng của hãng &nbsp;Nhật Bản nữa đ&acirc;́y. Sản ph&acirc;̉m sữa tắm với hương hoa hồng nhẹ dịu c&ugrave;ng với chiết xuất của sữa non gi&uacute;p bạn c&oacute; được một l&agrave;n da trắng sáng, mịn màng. Sữa tắm Perfect Whip còn có ưu đi&ecirc;̉m đó là có th&ecirc;̉ tạo v&ocirc; số bọt li ti gi&uacute;p chúng ta lấy đi mồ h&ocirc;i v&agrave; bụi 