INSERT INTO `product` VALUES ('147', 'Nước Hoa Vùng Kín Foellie Nâu', '215000', null, '6', null, null, null, '0', 'images/uploads/products/nula_cosmetic_product_1636564934.jpg', null, '2', '1', '<p><strong>TH&Ocirc;NG TIN SẢN PHẨM</strong></p>\r\n\r\n<p><br />\r\n<em><strong>Nước hoa v&ugrave;ng k&iacute;n</strong></em>&nbsp;Foellie Inner Perfume 5ml b&iacute; quyết gi&uacute;p &quot; c&ocirc; b&eacute; &quot; kh&ocirc;ng chỉ xinh xắn m&agrave; c&ograve;n trở n&ecirc;n ngọt ng&agrave;o quyến rũ, cho bạn những ph&uacute;t gi&acirc;y v&ocirc; c&ugrave;ng l&atilde;ng mạn</p>\r\n\r\n<p>Phụ nữ kh&ocirc;ng chỉ quyến rũ bởi nhan sắc, t&acirc;m hồn m&agrave; c&ograve;n c&oacute; một thứ vũ kh&iacute; v&ocirc; c&ugrave;ng chết người đ&oacute; ch&iacute;nh l&agrave; m&ugrave;i hương. Một ch&uacute;t mơ mộng, một ch&uacute;t ng&acirc;y ng&acirc;y bởi những hương thơm nồng ấm. Cả cơ thể đ&atilde; c&oacute; những hương thơm m&ecirc; người ấy th&igrave; tại sao ch&uacute;ng ta lại bỏ qua &quot;c&ocirc; b&eacute;&quot; nhỉ. C&oacute; lẽ ch&iacute;nh v&igrave; thế m&agrave; Foellie- một thương hiệu nổi tiếng của H&agrave;n Quốc đ&atilde; cho ra mắt d&ograve;ng nước hoa d&agrave;nh ri&ecirc;ng cho v&ugrave;ng k&iacute;n Foellie Inner Perfume 5ml.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa vung kin foellie den 4\" src=\"https://cocoshop.vn/uploads/shops/2020_04/nuoc-hoa-vung-kin-foellie-den-4.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mặc d&ugrave; kh&ocirc;ng phải l&agrave; sự đột ph&aacute; mới mẻ, v&igrave; trước đ&acirc;y đ&atilde; c&oacute; rất nhiều h&atilde;ng đ&atilde; sản xuất nước hoa v&ugrave;ng k&iacute;n. Thế nhưng c&aacute;c bạn h&atilde;y đến với Coco Shop v&agrave; d&ugrave;ng thử Foellie Inner Perfume, đảm bảo rằng hương thơm m&ecirc; đắm ấy sẽ kh&ocirc;ng l&agrave;m bạn v&agrave; người ấy thất vọng.</p>\r\n\r\n<p>Nếu bạn l&agrave; một t&iacute;n đồ của nước hoa, đ&atilde; trải nghiệm với những m&ugrave;i hương kh&aacute;c nhau của Dior, Lancome, Burberry, Calvin Klein,... v&agrave; c&aacute;c bạn muốn c&ocirc; b&eacute; của m&igrave;nh cung phải ngọt ng&agrave;o v&agrave; gợi cảm th&igrave; nước hoa v&ugrave;ng k&iacute;n Foellie Inner Perfume sẽ l&agrave; một lựa chọ m&agrave; c&aacute;c bạn kh&ocirc;ng thể n&agrave;o bỏ qua. Đ&acirc;y l&agrave; d&ograve;ng nước hoa v&ugrave;ng k&iacute;n nhưng hương thơm kh&ocirc;ng qu&aacute; thuần t&iacute;nh nữ n&ecirc;n cũng c&oacute; thể gọi l&agrave; nước hoa unisex</p>\r\n\r\n<p>Nước hoa v&ugrave;ng k&iacute;n c&oacute; m&ugrave;i hương tự nhi&ecirc;n, dễ chịu, kh&ocirc;ng cần phải băn khoăn m&ugrave;i n&agrave;y hợp m&ugrave;a n&agrave;o, hương kia hợp với dịp nọ. Thiết kế theo dạng nước hoa mini tinh tế tiện dụng, nước hoa v&ugrave;ng k&iacute;n Foellie c&oacute; thể bỏ t&uacute;i, &aacute;o, cầm tay&hellip;v&agrave; lu&ocirc;n giữ b&ecirc;n m&igrave;nh cho tất cả mọi trường hợp.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa vung kin foellie den 3\" src=\"https://cocoshop.vn/uploads/shops/2020_04/nuoc-hoa-vung-kin-foellie-den-3.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nước hoa v&ugrave;ng k&iacute;n&nbsp;<em><strong>Foellie</strong></em>&nbsp;Inner Perfume 5ml được thiết kế nhỏ gọn với dung t&iacute;ch 5ml. Lọ nước hoa xinh xắn từng m&ugrave;i hương kh&aacute;c nhau sẽ c&oacute; bao b&igrave; kh&aacute;c nhau. Tuy nhi&ecirc;n mỗi lọ đều to&aacute;t l&ecirc;n sự quyến rũ, ki&ecirc;u sa v&ocirc; c&ugrave;ng sang trọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nula Cosmetic</strong>&nbsp;với c&aacute;c m&ugrave;i:</p>\r\n\r\n<p><strong>M&agrave;u đen Eau de Bijou:</strong>&nbsp;huyền b&iacute;, sẽ l&agrave; lựa chọn h&agrave;ng đầu cho những c&ocirc; n&agrave;ng th&iacute;ch m&ugrave;i hương quyến rũ chết người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa vung kin foellie den 1\" src=\"https://cocoshop.vn/uploads/shops/2020_04/nuoc-hoa-vung-kin-foellie-den-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;u trắng Eau de Bon Bon:</strong>&nbsp;hương hoa anh đ&agrave;o, th&iacute;ch hợp cho những c&ocirc; n&agrave;ng iu sự dễ thương, cute ph&ocirc; mai que</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631524226340-nuoc-hoa-vung-kin-foellie-trang-1.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;u đỏ Eau de b&eacute;b&eacute;:&nbsp;</strong>nhẹ nh&agrave;ng, những c&ocirc; n&agrave;ng kh&ocirc;ng qu&aacute; y&ecirc;u th&iacute;ch m&ugrave;i hương, sự nổi bật n&ecirc;n thử em n&agrave;y nh&eacute;! hơi giống m&ugrave;i phấn rơm em b&eacute; nha, m&ugrave;i cực nhẹ lu&ocirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631524239469-nuoc-hoa-vung-kin-foellie-do-1.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;u n&acirc;u Eau de Foret:</strong>&nbsp;cho những c&ocirc; n&agrave;ng &quot;qu&yacute; sờ tộc&quot;. Hương thơm của em n&agrave;y kiểu rất trầm ấm, sang chảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631524253087-nuoc-hoa-vung-kin-foellie-nau-2.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;u hồng Eau de Fleur:&nbsp;</strong>tr&aacute;i c&acirc;y sẽ th&iacute;ch hợp với những n&agrave;ng th&iacute;ch sự tươi mới, năng động</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631524322263-nuoc-hoa-vung-kin-foellie-hong-3.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;u xanh dương Eau de Vogue:</strong>&nbsp;m&agrave;u y&ecirc;n ả của biển. Em n&agrave;y tỏa ra m&ugrave;i hương cực dễ chịu, rất nhẹ nh&agrave;ng, dễ d&agrave;ng giải tỏa căng thẳng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.cocolux.com/2021/09/images/products/1631524280930-nuoc-hoa-vung-kin-foellie-xanh-3.jpeg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Dầu hạt hướng dương</p>\r\n\r\n<p>- Dầu Jojoba</p>\r\n\r\n<p>- Dầu hạt maccadamia</p>\r\n\r\n<p>- Dầu Argan</p>\r\n\r\n<p>- Dầu l&aacute; Camellia Oleifera</p>\r\n\r\n<p>- Dầu hoa anh thảo buổi tối</p>\r\n\r\n<p>- Phytoncide: l&agrave; 1 chất được biết đến gi&uacute;p bạn giảm căng thẳng, mệt mỏi, bạn thư th&aacute;i hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;<em><strong>Nước hoa v&ugrave;ng k&iacute;n Foellie</strong></em>&nbsp;Eau De Innerb Perfume gi&uacute;p c&ocirc; b&eacute; sạch sẽ, kh&ocirc; tho&aacute;ng, dễ chịu</p>\r\n\r\n<p>- C&acirc;n bằng pH v&ugrave;ng k&iacute;n, giảm t&igrave;nh trạng ẩm ướt hoặc kh&ocirc; hạn</p>\r\n\r\n<p>- Ti&ecirc;u diệt vi khuẩn g&acirc;y hại, ngăn ngừa vi&ecirc;m nhiễm, nấm ngứa phụ khoa</p>\r\n\r\n<p>- Loại bỏ m&ugrave;i h&ocirc;i cơ thể</p>\r\n\r\n<p>- Lưu lại m&ugrave;i hương tinh tế, quyến rũ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa vung kin foellie den 2\" src=\"https://cocoshop.vn/uploads/shops/2020_04/nuoc-hoa-vung-kin-foellie-den-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Trọng lượng</strong>&nbsp;: 5ML</p>\r\n\r\n<p><strong>Xuất xứ&nbsp;</strong>: H&Agrave;N QUỐC</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;FOELLIE</p>\r\n\r\n<p><em><strong>Mỹ phẩm Nula Cosmetic</strong></em></p>\r\n\r\n<p><strong>Hướng dẫn sử dụng:</strong></p>\r\n\r\n<p>Bạn c&oacute; thể sử dụng theo nhiều c&aacute;ch ph&ugrave; hợp với nhu cầu của m&igrave;nh:</p>\r\n\r\n<p>- Nhỏ 1-2 giọt v&agrave;o quần l&oacute;t mỗi ng&agrave;y, dung dịch sẽ h&uacute;t đi m&ugrave;i kh&oacute; chịu v&agrave; tỏa ra hương dễ chịu một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p>- Khi tắm bồn: nhỏ 3~4 giọt v&agrave;o nước. M&ugrave;i hương dễ chịu của nước hoa sẽ lan tỏa gi&uacute;p bạn thư th&aacute;i.</p>\r\n\r\n<p>- Trong ng&agrave;y hẹn h&ograve;: Nhỏ 1~2 giọt l&ecirc;n quần l&oacute;t, cổ tay, cổ, g&aacute;y. M&ugrave;i hương của Secret Love sẽ tạo cho bạn một bầu kh&ocirc;ng kh&iac