<script type="text/javascript">
    $(document).ready(function(){
        $.ajaxSetup({
            headers: {
                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
            }
        });
        $("body").on('click', '.item_add', function(){
            var product_id = $(this).data('id');
            let url = "{{ route('add.to.cart') }}";
            // AJAX request
            $.ajax({
                url: url,
                method: "POST",
                data: {
                    'id': product_id,
                },
                success: function (response) {
                    $('div#cart_modal').html(response);
                    $("#popupCartModal").modal('show');
                    $('.product_in_cart').text(parseInt($('.product_in_cart').text()) + 1);
                },
            });
        });


        $("body").on('click', ".item-remove-from-cart-modal", function(){
            var id = $(this).data('id');
            if(confirm("Are you sure want to remove?")) {
                $.ajax({
                    url: '{{ route('remove.from.cart') }}',
                    method: "DELETE",
                    data: {
                        id: id
                    },
                    success: function (response) {
                        $('div#cart_modal').html(response);
                        $("#popupCartModal").modal('show');
                    },
                });
            }
        });

        $("body").on('click', ".item-remove-from-cart-list", function(){
            var id = $(this).data('id');
            if(confirm("Are you sure want to remove?")) {
                $.ajax({
                    url: '{{ route('remove.product.from.cart') }}',
                    method: "DELETE",
                    data: {
                        id: id
                    },
                    success: function (response) {
                        $('div.cart_product_list').html(response);
                        $('.product_in_cart').text(parseInt($('.product_in_cart').text()) - 1);
                    },
                });
            }
        });

        $("body").on('click', ".item-sub", function(){
            var id = $(this).data('id');
            var price = $(this).data('price')
            if(parseInt($(".item_number_" + id).text()) > 1){
                $(".item_number_" + id).text(parseInt($(".item_number_" + id).text()) - 1);
                $(".item_total_" + id).text(formatNumber((parseFloat(price) * parseInt($(".item_number_" + id).text())), '.', ',') + 'đ');
            } else {
                $(".item_number_" + id).text('1');
            }
            var quantity = parseInt($(".item_number_" + id).text());
            $.ajax({
                url: '{{ route('update.cart') }}',
                method: "patch",
                data: {
                    id: id,
                    quantity: quantity
                },
                success: function (response) {
                    $('div#cart_modal').html(response);
                    $("#popupCartModal").modal('show');
                }
            });
        });

